mapper.select:
select * from (select rownum as rnum,n.* from (select fund_no as fundNo, member_id as memberId, fund_name as fundName, fund_amount as fundAmount, fund_fees as fundFees, fund_category as fundCategory, fund_start as fundStart, fund_end as fundEnd, (to_date(fund_end, 'YYYY-MM-DD')- to_date(fund_start, 'YYYY-MM-DD'))as dateGap, fund_intro as fundIntro, fund_as as fundAs, fund_like as fundLike, fund_reportcount as fundReportcount, fund_warning as fundWarning, fund_filepath1 as fundFilepath1, fund_filepath2 as fundFilepath2, fund_filepath3 as fundFilepath3, fund_filepath4 as fundFilepath4, fund_filepath5 as fundFilepath5 from fund
where fund_category=#{category}
order by fund_no desc)n)
